import * as React from 'react';
import { Text, View } from 'react-native';

import { LinearGradient } from 'expo-linear-gradient';
import { SafeAreaView } from 'react-native-web';

import { withExpoSnack, styled} from 'nativewind';

const StyledView = styled(View)
const StyledText = styled(Text)


export default function HomeScreen() {
    return (

      <View className='flex-1'>
        <LinearGradient to-right colors={['black', 'mediumblue']} start={{ x: 0, y: 1 }} end={{ x: 1, y: 0 }} style={{flex:1}}>
        <SafeAreaView>
            <StyledView className="container h-12 justify-center bg-slate-300 items-center">
            <StyledText className="text-white-800">TogetherSport</StyledText>
            </StyledView>

        </SafeAreaView>



          <Text className='text-white'>Home!</Text>



        </LinearGradient>

      </View> 
    );
  }
