import * as React from 'react';
import { Text, View } from 'react-native';
import { styled } from 'nativewind';
import MapView from 'react-native-maps';
import { withExpoSnack } from 'nativewind';
import colors from "tailwindcss/colors";

const StyledView = styled(View, 'flex-1 items-center justify-center pt-5');
const StyledText = styled(Text);

const Localisation = () => {
  return (
    <StyledView>
      <MapView
        style={{ width: 300, height: 300 }} // Définissez la taille de la carte comme vous le souhaitez
        initialRegion={{
          latitude: 37.78825, // Latitude initiale de la carte
          longitude: -122.4324, // Longitude initiale de la carte
          latitudeDelta: 0.0922, // Variation de latitude
          longitudeDelta: 0.0421, // Variation de longitude
        }}
      >
        {/* Vous pouvez ajouter des marqueurs ici */}
        {/* <MapView.Marker
          coordinate={{ latitude: 37.78825, longitude: -122.4324 }}
          title="Mon marqueur"
          description="C'est mon marqueur personnalisé."
        /> */}
      </MapView>
    </StyledView>
  );
}

export default withExpoSnack(Localisation);







